sp_widget.client_script:
function($scope,spModal,spUtil,$rootScope) {
	/* widget controller */
	var c = this;
	c.affimage=function(idimage){
		console.log("pf image");
		spModal.open({
                   // title:'Image',
                    size:'lg',
                    widget:'widget_image_bill',
                    widgetInput:{image:idimage}

                }).then(function(){


   });
	}
	c.selectItem = function(idx,action) {  
		// Set the position indicator to the index value passed in on select
		c.billPos = idx;
		console.log("idx : "+idx);
		var id = c.data.bills[idx].sys_id;
		console.log(id);
		if(action == 'delete'){

			c.confirmDelete(id);	
		}
		if(action == 'update'){
			$rootScope.billID = id;
		$rootScope.$emit('selectBill', id);

		}
	}

	c.confirmDelete = function(idd){

		spModal.confirm("Are you sure you want to delete this Note record?").then(function(confirmed){
			if(confirmed){
				c.deleteBill(idd);
			}
		});
	}
	c.deleteBill = function(iddd) {
		c.server.get({
			action: 'deleteBill',
			billID:iddd
		}).then(function(r) {
			spUtil.addTrivialMessage("The record has been deleted.");
			console.log("position : "+c.billPos);
			c.data.bills.splice(c.billPos, 1);
		});
	}
}

