sp_widget.client_script:
function($scope,spUtil) {
	/* widget controller */
	var c = this;

	$scope.formBill=function() {
				c.myWidget = null;

		spUtil.get("80936ad61bcab30072fe2136ec4bcbd3").then(function(response) {
		    spUtil.update($scope);

			c.myWidget = response;
			console.log(response);
		});
	}


	$scope.list=function() {

		c.myWidget = null;
		spUtil.get("73ac4e551b02330072fe2136ec4bcb60").then(function(response) {
		    spUtil.update($scope);

			c.myWidget = response;
			console.log(response);
		});



	}
}

