sp_widget.client_script:
function($scope,spModal,spUtil,$filter,$rootScope) {
	var c = this;
$scope.range = function (size,start, end) {
    var ret = [];        
    console.log(size,start, end);

       if (size < end) {
        end = size;
        if(size<$scope.gap){
             start = 0;
        }else{
             start = size-$scope.gap;
        }

    }
    for (var i = start; i < end; i++) {
        ret.push(i);
    }        
     console.log(ret);        
    return ret;
};


		c.ctrlRead =function(data ) 
	{

	$scope.sortingOrder = sortingOrder;
	$scope.reverse = false;
	$scope.filteredItems = [];
	$scope.groupedItems = [];
	$scope.itemsPerPage = 5;
	$scope.pagedItems = [];
	$scope.currentPage = 0;
	/* $scope.items =[{"billIndex":1,"image":"","number":"EXP001605","process_date":"2019-05-29","title":"hj,jh,","value":"398.5733"},{"billIndex":2,"image":"","number":"EXP001604","process_date":"2019-04-10","title":"hgjj","value":"4855.1048"},{"billIndex":3,"image":"","number":"EXP001603","process_date":"2019-06-14","title":"kljl","value":"512.7062"},{"billIndex":4,"image":"","number":"EXP001602","process_date":"2019-04-09","title":"ouio","value":"178.3326"},{"billIndex":5,"image":"f4a2d2b61b5a330072fe2136ec4bcb99","number":"EXP001601","process_date":"2019-04-10","title":"eg","value":"4876.9772"},{"billIndex":6,"image":"497e31bd1bda730072fe2136ec4bcb6c","number":"EXP001599","process_date":"2019-06-28","title":"titre","value":"32449"},{"billIndex":7,"image":"92d922a51bd2730072fe2136ec4bcbed","number":"EXP001550","process_date":"2019-05-26","title":"test update","value":"569330"},{"billIndex":8,"image":"bf431ea11b92730072fe2136ec4bcbc0","number":"EXP001522","process_date":"2019-11-01","title":"tetst update99","value":"963"}] ; 
	console.log($scope.items);*/
	$scope.items =JSON.parse(c.data.tableData);
	//$scope.items=getData2();
	var searchMatch = function (haystack, needle) {
		if (!needle) {
			return true;
		}
		return haystack.toLowerCase().indexOf(needle.toLowerCase()) !== -1;
	};

	// init the filtered items
	$scope.search = function () {
		$scope.filteredItems = $filter('filter')($scope.items, function (item) {
			for(var attr in item) {
				if (searchMatch(item[attr], $scope.query))
					return true;
			}
			return false;
		});
		// take care of the sorting order
		if ($scope.sortingOrder !== '') {
			$scope.filteredItems = $filter('orderBy')($scope.filteredItems, $scope.sortingOrder, $scope.reverse);
		}
		$scope.currentPage = 0;
		// now group by pages
		$scope.groupToPages();
	};

	// calculate page in place
	$scope.groupToPages = function () {
		$scope.pagedItems = [];

		for (var i = 0; i < $scope.filteredItems.length; i++) {
			if (i % $scope.itemsPerPage === 0) {
				$scope.pagedItems[Math.floor(i / $scope.itemsPerPage)] = [ $scope.filteredItems[i] ];
			} else {
				$scope.pagedItems[Math.floor(i / $scope.itemsPerPage)].push($scope.filteredItems[i]);
			}
		}
	};

	$scope.range = function (start, end) {
		var ret = [];
		if (!end) {
			end = start;
			start = 0;
		}
		for (var i = start; i < end; i++) {
			ret.push(i);
		}
		return ret;
	};

	$scope.prevPage = function () {
		if ($scope.currentPage > 0) {
			$scope.currentPage--;
		}
	};

	$scope.nextPage = function () {
		if ($scope.currentPage < $scope.pagedItems.length - 1) {
			$scope.currentPage++;
		}
	};

	$scope.setPage = function () {
		$scope.currentPage = this.n;
	};

	// functions have been describe process the data for display
	$scope.search();

	// change sorting order
	$scope.sort_by = function(newSortingOrder) {
		if ($scope.sortingOrder == newSortingOrder)
			$scope.reverse = !$scope.reverse;

		$scope.sortingOrder = newSortingOrder;

		// icon setup
		$('th i').each(function(){
			// icon reset
			$(this).removeClass().addClass('icon-sort');
		});
		if ($scope.reverse)
			$('th.'+$scope.sortingOrder+' i').removeClass().addClass('icon-chevron-up');
		else
			$('th.'+$scope.sortingOrder+' i').removeClass().addClass('icon-chevron-down');
	};
		}

	//c.getData();
	c.ctrlRead(c.data.tableData );



	c.detail=function(id,name){
		console.log("detail");
		spModal.open({
                   title:'Detail of '+name,
                    //size:'lg',
                    widget:'view_sheet',
                    widgetInput:{idsheet:id}

                }).then(function(){


   });
	}


	c.selectItem = function(id,action) {  
		// Set the position indicator to the index value passed in on select
		//c.sheetPos = idx;
		console.log("id : "+id);
		//var id = c.data.sheets[idx].sys_id;

		if(action == 'delete'){

			c.confirmDelete(id);	
		}
		if(action == 'update'){
			$rootScope.sheetID = id;
		$rootScope.$emit('selectsheet', id);

		}
	}

	c.confirmDelete = function(idd){

		spModal.confirm("Are you sure you want to delete this Sheet record?").then(function(confirmed){
			if(confirmed){
				c.deletesheet(idd);
			}
		});
	}
	c.deletesheet = function(iddd) {

			c.data.action= 'deleteSheet';
			c.data.sheetID = iddd ;
		c.server.update().then(function(r) {

			console.log(r);
			spUtil.addTrivialMessage("The record has been deleted.");
			//$scope.data.tableData = c.data.tableData ; 
						c.ctrlRead(c.data.tableData );
			c.data.action = undefined;
					});
	}


}

